/// Installiert das eingebaute H1-Deck (komplett) aus der TSV-Konstanten unten.
/// Nutzt den (web-tauglichen) StreamingDeckWriter aus StorageService.
Future<(int cards, int notes)> installH1FullDeck() async {
  final writer = await StreamingDeckWriter.begin(
    title: 'H1 Fachgespräch (eingebaut, komplett)',
    sourceName: 'H1-Fachgespräch (eingebaut)',
  );

  final lines = const LineSplitter().convert(_H1_TSV.trim());
  for (final raw in lines) {
    if (raw.trim().isEmpty || raw.startsWith('#')) continue;
    final parts = raw.split('\t');
    if (parts.length < 3) continue;

    final nr = parts[0].trim();
    final q  = parts[1].trim();
    final a  = parts.sublist(2).join('\t').trim();

    if (q.isEmpty || a.isEmpty) continue;

    final id = _fnv64Hex('$nr\n$q\n$a');
    writer.appendCards([Flashcard(id: id, number: nr, question: q, answer: a)]);
  }

  await writer.finish();
  return (writer.cardCount, writer.unmatchedCount);
}

String _fnv64Hex(String s) {
  final mask64 = BigInt.parse('0xFFFFFFFFFFFFFFFF');
  var hash = BigInt.parse('0xcbf29ce484222325');
  final prime = BigInt.parse('0x100000001b3');
  for (final b in utf8.encode(s)) {
    hash = (hash ^ BigInt.from(b)) & mask64;
    hash = (hash * prime) & mask64;
  }
  return hash.toRadixString(16).padLeft(16, '0');
}

